INSERT INTO properties(additional_info,amenities,check_in_date,check_out_date,city,country,crawl_date,description,highlight_value,hotel_star_rating,image_count,image_urls,internet,landmark,latitude,longitude,occupancy,pageurl,property_address,property_id,property_name,property_type,qts,query_time_stamp,room_price,room_types,search_term,service_value,similar_hotel,sitename,things_to_do,things_to_note,uniq_id) VALUES ('Acceptance Rate:100 percent|Response Time:< 6 hours','WiFi|Free Breakfast|Newspaper|AC',STR_TO_DATE('04-08-2016', '%m-%d-%Y'),STR_TO_DATE('05-08-2016', '%m-%d-%Y'),'Kozhikode','India','2016-07-20','A new addition to the Raviz family, The Raviz Calicut is an iconic landmark in the heart of Calicut City. The 15 storey edifice is the epitome of understated luxury, tasteful excellence and culinary distinction. Located 28 kms from the international airport, and a stone''s throw away from the railway station and bus depot, Raviz Calicut is ideally suited for the business traveler.Be it the luxuriously appointed rooms or the refreshingly varied cuisine, ranging from the traditionally local to contemporary continental and exotically oriental. Take your pick from Keraleeyam, Raviz Cafe, The Pergola and GAO SHI - the Zircon Lounge Bar. Allow your taste buds a few days of ecstasy.If it''s just business that brings you to town, have your power conferences at The Majlis, The Empire or The Senate; depending on the numbers attending. But rest assured, the service will be exceptionally efficient, while being noticeably invisible.Soak in the ambience of a city rated by economics research firm Indicus Analytics as India''s second best city to reside in. A city that is famous for its unique culture and enthralling tradition. A city that has continuously contributed to the Indian freedom movement, Journalism, Malayalam literature & folklore. Tone up your body at the gym or unwind at the pool side. But no matter what, don''t miss out on the incredible experience of visiting the Favourite Kerala Ayurveda & Spa center.',NULL,NULL,4,'http://stay-imgs.stayzilla.com/resize/1200x800/113116/1048312.jpg|http://stay-imgs.stayzilla.com/resize/1200x800/113116/1048224.jpg|http://stay-imgs.stayzilla.com/resize/1200x800/113116/1048152.jpg|http://stay-imgs.stayzilla.com/resize/1200x800/113116/1048300.jpg',NULL,NULL,11.2600298,75.7884369,'3 Adults 1 Kid','https://www.stayzilla.com/stays/the-raviz-calicut-113116?checkInDate=04-08-2016&checkOutDate=05-08-2016&source=propertyCard','Mavoor Road, Kozhikode',113116,'The Raviz Calicut','Hotel','2016-07-20 12:10:45 +0000','2016-07-20 12:10:45 +0000','7872per night incl. tax','Double A/C Rooms',NULL,'Not Verified',NULL,'www.stayzilla.com',NULL,NULL,'431b69595fa50dbef388d52db64052e9');
